import React, { Component } from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import profile from './profile-image.jpg'
// import '/Assets/Styles/myStyles.scss'
import './myStyles.scss';
import { FaGithubAlt,FaTwitterSquare } from 'react-icons/fa';

 class SocialCard extends Component {
  fnGoToGithubPage=()=>{
    window.location.href=`https://github.com/gloria-kambua`;
  }
  render() {
    return (
      <div>
        <Card  className='social-card'>
          <Card.Img variant="top" src={profile}/>
          <Card.Body>
            {/* <Card.Title>Card Title</Card.Title>
            <Card.Text>
              Some quick example text to build on the card title and make up the
              bulk of the card's content.
            </Card.Text> */}
            <Row>
              <Col>
                <Button variant="light" onClick={fnGoToGithubPage}><FaGithubAlt/>Github</Button>
              </Col>
              <Col>
                <Button variant="secondary"><FaTwitterSquare/>Twitter</Button>
              </Col>
            </Row>
          </Card.Body>
        </Card>
      </div>
    )
  }
}

export default SocialCard
